mapper.update:
update organizations set short_name = #{shortName}, name = #{name}, organization_opf = #{organizationOPF}, organization_type = #{organizationType}, ogrn = #{ogrn}, inn = #{inn}, activity_started_at = #{activityStartedAt},
activity_finished_at = #{activityFinishedAt},
updated_at = NOW() where 1 = 1 and id = #{id};
